meta.script:
        var optsTag = '
';
        var scTagOpen = '

';
        var scTagClos = '';

        var curSubs = undefined;
        var curSc = undefined;
        var curSents = [];
        var curOpts = [];
        function appendMain(tagStr, cbf){
            if(cbf == undefined){
                cbf = function(){};
            }
            var $new = $(tagStr);
            $('#avgMain').append($new);
            $new.show('slow' , cbf)
            .delay(1000000)
            .queue(cbf);
        }
        function newSc(){
            if(scs.length == 0){
                $('#next').hide();
                return false;
            }
            curSc = scs.pop(); appendMain(scTagOpen + curSc.title + scTagClos);
            curSents = curSc.pref.sents;
            curOpts = curSc.pref.opts;
            curSubs = curSc.subs;
            return true;
        }
        function genOpts(){
            $(optsTag).appendTo($('#avg')).show('slow');
            $('' + curOpts[0].sent
                + '').appendTo('#opts').show('slow');
            for(var i = 1; i < curOpts.length; i++){
                $('

' + curOpts[i].sent
                    + '').appendTo('#opts')
                .show('slow');
            }
        }
        function changeSc(name){
            curSents = curSubs[name].sents;
            curOpts = curSubs[name].opts;
        }
        function nextSent(){
            while(curSents.length == 0){
                if(curOpts.length == 0){
                    newSc();
                }
                else{
                    $('#next').hide();
                    genOpts();
                }
                $('html,body').animate({scrollTop: $('#avgMain').prop("scrollHeight")}, 500);
                return;
            }
            var e = undefined;
            var acc = '
';
                while(curSents.length != 0){
                    e = curSents.pop();
                    if(e.slice(-1) == '@') break;
                    acc += '
' + e + '';
                    e = undefined;
                }
                if(e != undefined){
                    acc += '
' + e.substring(0, e.length - 1) + '';
                }
                appendMain(acc + '', function(){
                    if(curSents.length == 0 && curOpts.length == 0) nextSent();
                });

            $('html,body').animate({scrollTop: $('#avgMain').prop("scrollHeight")}, 500);
        }
        $(document).ready(function(){
            nextSent();
            $('#next').click(function(){
                nextSent();
            });
            $(document).on('click', '.opt', function () {
                changeSc(this.getAttribute("opt"));
                $('
' + this.textContent + '')
                .appendTo('#avgMain').show('slow');
                $('#opts').remove();
                $('#next').show();
            });
            $(document).keypress(function(e) {
                if(e.which == 13 || e.width == 32) {
                    if($('#next').is(":visible")){
                        $('#next').click();
                    }
                }
            });
        });
